mapper.select:
select member_id, member_identification, member_password, member_name, member_phone, member_email, member_file_system_name, member_type, member_drive_register_date, member_point, apply_id, apply_course, apply_location, apply_date, apply_register_date, starter_member_id, veteran_member_id, apply_status from tbl_member m join tbl_apply a on m.member_id = a.starter_member_id
and apply_location like CONCAT('%', #{info.applyLocation}, '%')
and apply_date = #{info.applyDate}
order by apply_id desc LIMIT #{criteria.offset}, #{criteria.amount}

select count(apply_id) from tbl_member m join tbl_apply a on m.member_id = a.starter_member_id
and apply_location like CONCAT('%', #{info.applyLocation}, '%')
and apply_date = #{info.applyDate}

mapper.update:
update tbl_apply
set apply_status = if(apply_status = '1' , '0', '1')
where apply_id = #{applyId}

update tbl_apply
set veteran_member_id = if(veteran_member_id, null, #{memberId})
where apply_id = #{applyId}

mapper.select:
select count(apply_id) from tbl_member m join tbl_apply a on m.member_id = a.starter_member_id
and apply_location like CONCAT('%', #{info.applyLocation}, '%')
and apply_date = #{info.applyDate}

select count(apply_id)
from tbl_member m join tbl_apply a on m.member_id = a.starter_member_id

    and apply_location like CONCAT('%', #{info.applyLocation}, '%')


    and apply_date = #{info.applyDate}

and veteran_member_id != #{info.memberId}

select apply_id, apply_course, apply_location, apply_date, apply_register_date, starter_member_id, veteran_member_id, apply_status
from tbl_apply where starter_member_id = #{memberId}
order by apply_id desc

    limit #{standards.offset}, #{standards.rowCount}
